class MGHomeModel {
  List<Slide>? _slide;
  List<VideoModel>? _hotvideo;
  Tv? _tv;
  List<Video>? _video;

  List<Slide>? get slide => _slide;
  List<VideoModel>? get hotvideo => _hotvideo;
  Tv? get tv => _tv;
  List<Video>? get video => _video;

  MGHomeModel(
      {List<Slide>? slide,
      List<VideoModel>? hotvideo,
      Tv? tv,
      List<Video>? video}) {
    _slide = slide;
    _hotvideo = hotvideo;
    _tv = tv;
    _video = video;
  }

  MGHomeModel.fromJson(dynamic json) {
    if (json["slide"] != null) {
      _slide = [];
      json["slide"].forEach((v) {
        _slide?.add(Slide.fromJson(v));
      });
    }
    if (json["hotvideo"] != null) {
      _hotvideo = [];
      json["hotvideo"].forEach((v) {
        _hotvideo?.add(VideoModel.fromJson(v));
      });
    }
    _tv = json["tv"] != null ? Tv.fromJson(json["tv"]) : null;
    if (json["video"] != null) {
      _video = [];
      json["video"].forEach((v) {
        _video?.add(Video.fromJson(v));
      });
    }
  }

  Map<String, dynamic> toJson() {
    var map = <String, dynamic>{};
    if (_slide != null) {
      map["slide"] = _slide?.map((v) => v.toJson()).toList();
    }
    if (_hotvideo != null) {
      map["hotvideo"] = _hotvideo?.map((v) => v.toJson()).toList();
    }
    if (_tv != null) {
      map["tv"] = _tv?.toJson();
    }
    if (_video != null) {
      map["video"] = _video?.map((v) => v.toJson()).toList();
    }
    return map;
  }
}

